package org.asdc.medhub.Repository;import org.asdc.medhub.Utility.Model.DatabaseModels.Blogs;import org.springframework.data.jpa.repository.JpaRepository;import java.util.List;/** * Repository for database operations related to blogs. */public interface BlogsRepository extends JpaRepository<Blogs, Long> {       /**        * Retrieves all blogs associated with a specific doctor.        *        * @param doctorId The ID of the doctor.        * @return A list of blogs associated with the specified doctor.        */       List<Blogs> findAllByDoctorId(Integer doctorId);       /**        * Retrieves all blogs where the body contains the specified string.        *        * @param body The string to search for within the body of the blogs.        * @return A list of blogs where the body contains the specified string.        */       List <Blogs> getAllByBodyContains(String body);}