package org.asdc.medhub.Repository;import org.asdc.medhub.Utility.Model.DatabaseModels.SpecializationOfDoctor;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.stereotype.Repository;/** * Repository for database operations related to the specialization of doctors */@Repositorypublic interface SpecializationOfDoctorRepository extends JpaRepository<SpecializationOfDoctor, Integer> {    /**     * Deletes all records of specializations associated with a specific doctor     *     * @param doctorId the ID of the doctor whose specializations need to be deleted     */    void deleteAllByDoctorId(int doctorId);}