package org.asdc.medhub.Service.Interface;import org.asdc.medhub.Utility.Model.RequestModels.BlogsModel;import org.asdc.medhub.Utility.Model.ResponseModel;import org.asdc.medhub.Utility.Model.ResponseModels.BlogsDetail;import java.util.List;/** * Service interface for handling blog-related functionalities. */public interface IBlogsService {    /**     * Retrieves all blog posts.     * @return A ResponseModel containing the list of blog posts.     */    ResponseModel<List<BlogsDetail>> getAllBlogsPostsForDoctors();    /**     * retrieves all blogs for the patients by the doctorId.     * @param doctorId by the which a patient is able to find out the details for the blogs for a specific doctor     * @return A ResponseModel containing the list of blog posts along with the doctor name     */    ResponseModel<List<BlogsDetail>> getAllBlogsPostsForPatients(int doctorId);    /**     * Creates a new blog post.     * @param blogsModel The details of the blog post to create.     * @return A ResponseModel containing the status message of the operation.     */    ResponseModel<String> createBlogPost(BlogsModel blogsModel);}