package org.asdc.medhub.Utility.Model.DatabaseModels;import com.fasterxml.jackson.annotation.JsonIgnore;import jakarta.persistence.*;import lombok.AllArgsConstructor;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;import org.asdc.medhub.Utility.Constant.DatabaseConstants;import java.sql.Timestamp;/** * Entity class representing the Blogs table in the database. */@Getter@Setter@AllArgsConstructor@NoArgsConstructor@Entity(name = DatabaseConstants.BlogsTable.tableName)public class Blogs {    /**     * Id for thr Blogs     */    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    protected int id;    /**     * Title for thr Blogs     */    @Column(name = DatabaseConstants.BlogsTable.Columns.blogTitle)    private String blogTitle;    /**     * Body for thr Blogs with the length of characters to 5000     */    @Column(name = DatabaseConstants.BlogsTable.Columns.blogBody ,length = 5000 )    private String body;    /**     * Timestamp when Blogs were created     */    @Column(name = DatabaseConstants.BlogsTable.Columns.createdAt)    private Timestamp createdAt;    /**     * Timestamp when Blogs were updated     */    @Column(name = DatabaseConstants.BlogsTable.Columns.updatedAt)    private Timestamp updatedAt;    /**     * Many-to-one relationship with the Doctor entity, representing the author of the blog     */    @ManyToOne    @JoinColumn(name= DatabaseConstants.BlogsTable.Columns.doctorId)    @JsonIgnore    private Doctor doctor;}