package org.asdc.medhub.Utility.Model.ResponseModels;import com.fasterxml.jackson.annotation.JsonFormat;import jakarta.validation.constraints.NotBlank;import jakarta.validation.constraints.NotNull;import lombok.AllArgsConstructor;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;import java.sql.Timestamp;@Getter@Setter@NoArgsConstructor@AllArgsConstructorpublic class BlogsDetail {    /**     * Blog post ID     */    @NotNull    private int id;    /**     * Title of the blog post     */    @NotBlank    private String title;    /**     * Description or content of the blog post     */    @NotBlank    private String description;    /**     * Timestamp indicating when the blog post was created     */    @JsonFormat(shape = JsonFormat.Shape.STRING, pattern = "yyyy-MM-dd'T'HH:mm:ss.SSSX")    private Timestamp createdAt;    /**     * Timestamp indicating when the blog post was last updated     */    @JsonFormat(shape = JsonFormat.Shape.STRING, pattern = "yyyy-MM-dd'T'HH:mm:ss.SSSX")    private Timestamp updatedAt;    /**     * doctorName indicating which doctor wrote the blog     */    private String doctorName;}